mapper.select:
SELECT s.snsNo, s.userNo, s.snsContent, s.snsCreateTime, u.userProfileImage , u.userNickname FROM sns s, user u WHERE s.userNo = u.userNo AND NOT s.userNo IN
#{item}
ORDER BY s.snsNo DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE sns
    SET snsContent = #{snsContent}, snsUpdateTime = now()
    WHERE snsNo = #{snsNo}

mapper.select:
SELECT snsImageNo, snsImageUrl
    FROM snsImage
    WHERE snsNo = #{snsNo}
