mapper.select:
SELECT userEmail, userPassword, userName, userProfileImage, userLocation, userAge, userGender, userNickname, userMBTI
FROM user
WHERE userEmail = #{userEmail};

mapper.update:
UPDATE user
SET userProfileImage = #{userProfileImage}, userLocation = #{userLocation}, userAge = #{userAge}, userGender = #{userGender}, userNickname = #{userNickname}, userMBTI = #{userMBTI}
WHERE userEmail = #{userEmail};
