mapper.insert:
INSERT INTO user(userEmail, userPassword, userName, userProfileImage, userLocation, userAge, userGender, userNickname, userMBTI)
    VALUES( #{userEmail}, #{userPassword}, #{userName}, #{userProfileImage}, #{userLocation}, #{userAge}, #{userGender}, #{userNickname}, #{userMBTI} );
INSERT INTO campStyle(userNo, campStyle1, campStyle2, campStyle3, campStyle4, campStyle5, campStyle6)
    VALUES ( (SELECT userNo FROM user WHERE userEmail = #{userEmail}), #{campStyle1}, #{campStyle2}, #{campStyle3}, #{campStyle4}, #{campStyle5}, #{campStyle6} );
INSERT INTO campEquip(userNo, campEquipTent, campEquipSleepingbag, campEquipBurner, campEquipBrazier, campEquipEtc1, campEquipEtc2, campEquipEtc3, campEquipEtc4, campEquipEtc5)
    VALUES ( (SELECT userNo FROM user WHERE userEmail = #{userEmail}), #{campEquipTent}, #{campEquipSleepingbag}, #{campEquipBurner}, #{campEquipBrazier}, #{campEquipEtc1}, #{campEquipEtc2}, #{campEquipEtc3}, #{campEquipEtc4}, #{campEquipEtc5} );

mapper.delete:
DELETE FROM campStyle WHERE userNo = (SELECT userNo from user WHERE userEmail = #{userEmail});
DELETE FROM campEquip WHERE userNo = (SELECT userNo from user WHERE userEmail = #{userEmail});
DELETE FROM follow WHERE userNo = (SELECT userNo from user WHERE userEmail = #{userEmail});
DELETE FROM follow WHERE followUserNo = (SELECT userNo from user WHERE userEmail = #{userEmail});
DELETE FROM user WHERE userEmail = #{userEmail};
